import React, { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import TaskColumn from "./components/TaskColumn";
import TaskModal from "./components/TaskModal";
import Button from "./components/Button";

const initialColumns = [
  { id: "general", title: "General Information", tasks: [] },
  { id: "backlog", title: "Backlog", tasks: [] },
  { id: "in-progress", title: "In Progress", tasks: [] },
  { id: "paused", title: "Paused", tasks: [] },
  { id: "launch", title: "Ready for Launch", tasks: [] },
];

const TaskManager = () => {
  const [columns, setColumns] = useState(initialColumns);
  const [modalOpen, setModalOpen] = useState(false);
  const [newTask, setNewTask] = useState({ title: "", assignees: [], status: "backlog", startDate: "", endDate: "" });

  const addTask = () => {
    setColumns((prev) =>
      prev.map((col) =>
        col.id === newTask.status ? { ...col, tasks: [...col.tasks, newTask] } : col
      )
    );
    setModalOpen(false);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="p-6 bg-gray-100 min-h-screen">
        <h1 className="text-xl font-bold mb-4">Task Manager</h1>
        <div className="flex gap-4">
          {columns.map((col) => (
            <TaskColumn key={col.id} column={col} />
          ))}
        </div>
        <Button onClick={() => setModalOpen(true)}>Add Task</Button>
        {modalOpen && <TaskModal setNewTask={setNewTask} addTask={addTask} setModalOpen={setModalOpen} />}
      </div>
    </DndProvider>
  );
};

export default TaskManager;
