import React, { useState } from "react";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { Button, Input, Select, Card, CardContent } from "@/components/ui";

const initialColumns = [
  { id: "general", title: "General Information", tasks: [] },
  { id: "backlog", title: "Backlog", tasks: [] },
  { id: "in-progress", title: "In Progress", tasks: [] },
  { id: "paused", title: "Paused", tasks: [] },
  { id: "launch", title: "Ready for Launch", tasks: [] },
];

const TaskManager = () => {
  const [columns, setColumns] = useState(initialColumns);
  const [modalOpen, setModalOpen] = useState(false);
  const [newTask, setNewTask] = useState({ title: "", assignees: [], status: "backlog", startDate: "", endDate: "" });

  const addTask = () => {
    setColumns((prev) =>
      prev.map((col) =>
        col.id === newTask.status ? { ...col, tasks: [...col.tasks, newTask] } : col
      )
    );
    setModalOpen(false);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="p-6 bg-gray-100 min-h-screen">
        <h1 className="text-xl font-bold mb-4">Task Manager</h1>
        <div className="flex gap-4">
          {columns.map((col) => (
            <TaskColumn key={col.id} column={col} />
          ))}
        </div>
        <Button onClick={() => setModalOpen(true)}>Add Task</Button>
        {modalOpen && <TaskModal setNewTask={setNewTask} addTask={addTask} setModalOpen={setModalOpen} />}
      </div>
    </DndProvider>
  );
};

const TaskColumn = ({ column }) => {
  return (
    <div className="w-64 bg-white p-4 rounded-lg shadow">
      <h2 className="font-semibold mb-2">{column.title}</h2>
      {column.tasks.map((task, index) => (
        <TaskCard key={index} task={task} />
      ))}
    </div>
  );
};

const TaskCard = ({ task }) => {
  return (
    <Card className="mb-2">
      <CardContent>
        <p className="font-semibold">{task.title}</p>
      </CardContent>
    </Card>
  );
};

const TaskModal = ({ setNewTask, addTask, setModalOpen }) => {
  return (
    <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-gray-900 bg-opacity-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-96">
        <h2 className="text-lg font-semibold mb-4">Add Card Modal</h2>
        <Input placeholder="Title" onChange={(e) => setNewTask((prev) => ({ ...prev, title: e.target.value }))} />
        <Select onChange={(e) => setNewTask((prev) => ({ ...prev, status: e.target.value }))}>
          <option value="backlog">Backlog</option>
          <option value="in-progress">In Progress</option>
          <option value="paused">Paused</option>
          <option value="launch">Ready for Launch</option>
        </Select>
        <Input placeholder="Start Date" type="date" onChange={(e) => setNewTask((prev) => ({ ...prev, startDate: e.target.value }))} />
        <Input placeholder="End Date" type="date" onChange={(e) => setNewTask((prev) => ({ ...prev, endDate: e.target.value }))} />
        <div className="mt-4 flex gap-2">
          <Button onClick={addTask}>Add</Button>
          <Button onClick={() => setModalOpen(false)} variant="secondary">Cancel</Button>
        </div>
      </div>
    </div>
  );
};

export default TaskManager;
